import React from "react";
import axios from "axios";
import { getTranslationData } from "../lib/translations";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      phrase: "",
      type: "",
      translated: "",
    };

    //bind methods here:
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.getTranslation = this.getTranslation.bind(this);
  }

  //handles change for select tag
  handleChange(event) {
    this.setState({ type: event.target.value });
  }

  handleFormSubmit(event) {
    event.preventDefault();
    this.setState({ phrase: event.target.value });
    console.log("current state of phrase:", this.state.phrase);
    this.getTranslation();
  }

  handleInputChange(event) {
    this.setState({ phrase: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("state of type is saved", this.state.type);
  }

  getTranslation() {
    event.preventDefault()
    axios
      .post(
        `https://api.funtranslations.com/translate/${this.state.type}.json?text=${this.state.phrase}`, newObj
      )
      .then(() => {
        let temp = {
          type: '',
          phrase: ''
        }

      })
      .catch((err) => {
        console.log("Error in getTranslation", err);
      });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Select Type:
            <select value={this.state.type} onChange={this.handleChange}>
              <option value="yoda">Yoda</option>
              <option value="sith">Sith</option>
              <option value="gungan">Gungan</option>
              <option value="huttese">Huttese</option>
              <option value="mandalorian">Mandalorian</option>
              <option value="cheunh">Cheunh</option>
            </select>
          </label>
          <input type="submit" value="SUBMIT" />
        </form>

        <form onSubmit={this.handleFormSubmit}>
          <label>
            Enter phrase here:
            <textarea
              value={this.state.phrase}
              onChange={this.handleInputChange}
            />
          </label>
          <input type="submit" value="SUBMIT" />
        </form>
        <p>Translation here:</p>
        {this.state.translated}
      </div>
    );
  }
}

export default Form;
