import React from "react";
import axios from "axios";
import { getTranslationData } from "../lib/translations";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      phrase: "",
      type: "",
      translated: "",
    };

    //bind methods here:
    this.handleChange = this.handleChange.bind(this);
    this.handleSelectSubmit = this.handleSelectSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getTranslation = this.getTranslation.bind(this);
  }

  handleChange(event) {
    let key = event.target.name;
    let value = event.target.value;
    this.setState({ [key]: value });
  }

  handleSelectSubmit(event) {
    event.preventDefault();
    this.setState({ type: event.target.value });
    console.log("this.state.type in handleselectsubmit", this.state.type);
  }

  handleInputChange(event) {
    this.setState({ phrase: event.target.value });
    console.log("this.state.phrase", this.state.phrase);
  }

  handleSubmit(event) {
    event.preventDefault();
    this.getTranslation();
  }

  getTranslation() {
    axios
      .post(
        `https://api.funtranslations.com/translate/${this.state.type}.json?text=${this.state.phrase}`
      )
      .then((results) => {
        console.log("Results here:", contents);
        this.setState({ translated: results.data });
      })
      .catch((err) => {
        console.log("Error in getTranslation", err);
      });
  }

  render() {
    return (
      <div>
        {/* <form onSubmit={this.handleSelectSubmit}> */}
        <label>
          Select Type:
          <select
            name="type"
            value={this.state.type}
            onChange={this.handleChange}
          >
            <option value="yoda">Yoda</option>
            <option value="sith">Sith</option>
            <option value="gungan">Gungan</option>
            <option value="huttese">Huttese</option>
            <option value="mandalorian">Mandalorian</option>
            <option value="cheunh">Cheunh</option>
          </select>
        </label>
        {/* </form> */}
        <h5>Enter Phrase Here:</h5>
        <input type="text" onChange={this.handleInputChange}></input>
        <div>
          <button type="submit" onClick={this.handleSubmit}>
            {" "}
            SUBMIT{" "}
          </button>
        </div>
        <p>Translation here:</p>
        {this.state.translated}
      </div>
    );
  }
}

export default Form;
