import React from "react";
import axios from "axios";
import { getTranslationData } from "../lib/translations";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      phrase: "",
      type: "",
      translated: "",
    };

    //bind methods here:
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getTranslation = this.getTranslation.bind(this);
  }

  handleChange(event) {
    this.setState({ type: event.target,value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.getTranslation();
  }

  getTranslation() {
    axios
      .post(
        `https://api.funtranslations.com/translate/${this.state.type}.json?text=${this.state.phrase}`
      )
      .then((results) => {
        console.log("Results here:", contents);
        this.setState({ translated: contents.translated });
      })
      .catch((err) => {
        console.log("Error in getTranslation", err);
      });
  }

  render() {
    return (
      <div>
        <label>
          Select Type:
          <select
            name="type"
            value={this.state.type}
            onChange={this.handleChange}
          >
            <option value="yoda">Yoda</option>
            <option value="sith">Sith</option>
            <option value="gungan">Gungan</option>
            <option value="huttese">Huttese</option>
            <option value="mandalorian">Mandalorian</option>
            <option value="cheunh">Cheunh</option>
          </select>
        </label>
        <button onClick={this.handleSelectSubmit}> SUBMIT</button>

        <h5>Enter Phrase Here:</h5>
        <input
          name="phrase"
          type="string"
          value={this.state.phrase}
          onChange={this.handleInputChange}
        ></input>
        <div>
          <button type="submit" onClick={this.handleSubmit}>
            {" "}
            SUBMIT{" "}
          </button>
        </div>
        <p>Translation here:</p>
        {this.state.translated}
      </div>
    );
  }
}

export default Form;
