import React from "react";
import axios from "axios";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      phrase: "",
      type: "",
      translated: "",
    };

    //bind methods here:
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleChange(event) {
    this.setState({ type: event.state.value });
  }

  handleInputChange(event) {
    this.setState({ phrase: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.getTranslation();
  }

  getTranslation() {
    axios
      .post(`https://api.funtranslations.com/translate/${this.state.type}.json`)
      .then((results) => {
        console.log("Results here:", results);
        this.setState({ translated: results.data });
      });
  }

  render() {
    return (
      <div>
        <label>
          Select Type:
          <select value={this.state.value} onChange={this.handleChange}>
            <option value="yoda">Yoda</option>
            <option value="sith">Sith</option>
            <option value="gungan">Gungan</option>
            <option value="huttese">Huttese</option>
            <option value="mandalorian">Mandalorian</option>
            <option value="cheunh">Cheunh</option>
          </select>
        </label>
        <h5>Enter Phrase Here:</h5>
        <input type="text" onChange={this.handleInputChange}></input>
        <div>
          <button type="submit" onClick={this.handleSubmit}>
            {" "}
            SUBMIT{" "}
          </button>
        </div>
      </div>
    );
  }
}

export default Form;
